mapper.select:
select id, userId, title,goal
from learning_plan
where userId = #{userId};

select id, userId, title, goal, status, createdAt, updatedAt
from learning_plan
where id = #{id} and userId = #{userId};

select id, title, goal, status
from learning_plan
where status = 'ACTIVE';

mapper.update:
update learning_plan
set title = #{title}, goal = #{goal}, status = #{status}
where id = #{id} and userId = #{userId};
