mapper.insert:
insert into videos (
id,userId,title,url, tags, category, description, duration
)values (
#{id},#{userId},#{title},#{url}, #{tags}, #{category}, #{description}, #{duration}
)

mapper.select:
select id,userId,userName,title,views,likes,
       category,duration,v.createdAt
from videos


        and date(createdAt) >= #{startTime}


        and date(createdAt) <= #{endTime}

mapper.update:
update videos
set likes = case id

    when #{item.videoId} then likes + #{item.delta}

end
where id in

    #{item.videoId}
